mapper.select:
SELECT *, ROUND( SQRT(POW(69.1 * (latitude - #{latitude}), 2) + POW(69.1 * (#{longitude} - longitude) * COS(latitude / 57.3), 2) ),2) AS distance
	FROM company_info 
	WHERE 1=1 
	
		AND member_id in  #{item}  
	
	HAVING distance <= #{distance}
	ORDER BY distance ASC

SELECT *, ROUND( SQRT(POW(69.1 * (latitude - #{latitude}), 2) + POW(69.1 * (#{longitude} - longitude) * COS(latitude / 57.3), 2) ),2) AS distance FROM company_info WHERE 1=1
AND is_certification = #{isCertification}
AND ( company_tag LIKE CONCAT('%', #{keyword}, '%') OR real_name LIKE CONCAT('%', #{keyword}, '%') OR mobile LIKE CONCAT('%', #{keyword}, '%') OR company_name LIKE CONCAT('%', #{keyword}, '%') )
HAVING distance <= #{distance}
ORDER BY distance ASC

SELECT * FROM company_info 
	WHERE 1=1 
	
		AND member_id in  #{item}
